css:
.kontematik-plugin__button {
background: #EAF0F6;
font-size: 14px;
padding: 4px 8px;
display: flex;
align-items: center;
justify-content: center;
color: #506e91;
border: solid 1px #cbd6e2;
border-radius: 3px;
font-weight: 400;
}
.kontematik-plugin__button:hover, .kontematik-plugin__button:focus, .kontematik-plugin__button:active {
background-color: #fff;
border: 1px solid #cbd6e2;
color: #506e91;
font-weight: 400;
}
.kontematik-plugin__button-logo {
height: 16px;
width: auto;
display: block;
margin-left: 4px;
}
